requestor.addRequestInterceptor(new RequestInterceptor() {

    public void interceptor(RequestInterceptorContext request) {

        request.setHeader("A-Request-Interceptor-Header", "It Works!");

    }

});

requestor.addResponseInterceptor(new ResponseInterceptor() {

    public void interceptor(Request request, ResponseInterceptorContext response) {

        response.setHeader("A-Response-Interceptor-Header", "It Works!");

    }

});
